package main

const aliasTemplate = `// nolint
// DO NOT EDIT Code generated by aliasgen tool (github.com/rhuairahrighairidh/aliasgen).
package {{.PackageName}}
{{ if .Imports }}
import ({{ range .Imports }}
	"{{.}}"{{end}}
)
{{end -}}

{{ if .Consts }}
const ({{ range .Consts }}
	{{.Name}} = {{.Pkg}}.{{.Name}}{{end}}
)
{{end -}}

{{if .HasVarsOrFuncs}}
var ({{if .Funcs}}
	// function aliases{{ range .Funcs }}
	{{.Name}} = {{.Pkg}}.{{.Name}}{{end}}{{end}}{{if .HasVarsAndFuncs}}
{{end}}{{if .Vars}}
	// variable aliases{{ range .Vars }}
	{{.Name}} = {{.Pkg}}.{{.Name}}{{end}}{{end}}
)
{{end -}}

{{ if .Types }}
type ({{ range .Types }}
	{{.Name}} = {{.Pkg}}.{{.Name}}{{end}}
)
{{end}}
`

