type TemplateData struct {
	PackageName string
	Imports     []string
	Consts      []Alias
	Vars        []Alias
	Funcs       []Alias
	Types       []Alias
}

func (td TemplateData) HasVarsOrFuncs() bool {
	return len(td.Vars) > 0 || len(td.Funcs) > 0
}
func (td TemplateData) HasVarsAndFuncs() bool {
	return len(td.Vars) > 0 && len(td.Funcs) > 0
}

type Alias struct {
	Pkg  string
	Name string
}
